# Simple Calculator
# A command-line calculator built step by step

def add(a, b):
    """Add two numbers and return the result."""
    return a + b

def subtract(a, b):
    """Subtract b from a and return the result."""
    return a - b

def multiply(a, b):
    """Multiply two numbers and return the result."""
    return a * b

def divide(a, b):
    """Divide a by b. Returns None if b is zero."""
    if b == 0:
        print("Error: Cannot divide by zero!")
        return None
    return a / b

def display_menu():
    """Display the calculator menu."""
    print("\n=== Calculator ===")
    print("1. Add")
    print("2. Subtract")
    print("3. Multiply")
    print("4. Divide")
    print("5. Exit")

def get_number(prompt):
    """Get a number from the user."""
    value = input(prompt)
    return float(value)

def get_choice():
    """Get the user's menu choice."""
    return input("Enter your choice (1-5): ")

